import json
from typing import Any, Dict, Union


def normalize_unicode_data(data: Union[str, Dict[str, Any]]) -> Dict[str, Any]:
    """
    Нормализует данные в формате переменной a для читаемого вывода.
    
    Функция принимает JSON строку или словарь в формате переменной a и возвращает 
    его в нормализованном виде с декодированными Unicode символами в читаемом формате.
    
    Args:
        data: JSON строка или словарь с данными в формате переменной a
    
    Returns:
        Нормализованный словарь с читаемыми Unicode символами
    """
    # Если данные пришли как JSON строка, парсим их
    if isinstance(data, str):
        try:
            data = json.loads(data)
        except json.JSONDecodeError:
            return {}
    
    # Если данные уже словарь, просто возвращаем их
    # json.loads уже декодирует Unicode escape-последовательности
    if isinstance(data, dict):
        return data
    
    return {}


def print_normalized(data: Union[str, Dict[str, Any]], indent: int = 2) -> None:
    """
    Выводит данные в формате переменной a в читаемом JSON формате с русскими символами.
    
    Args:
        data: JSON строка или словарь с данными в формате переменной a
        indent: Отступ для форматирования JSON (по умолчанию 2)
    """
    normalized = normalize_unicode_data(data)
    print(json.dumps(normalized, ensure_ascii=False, indent=indent, default=str))


